INSERT INTO text (id, text) VALUES (489,'Mescla rara de fotógrafo e repórter, com texto primoroso e alma de artista, o carioca Marcos Sá Corrêa é, antes de tudo, um apaixonado pela natureza.');
INSERT INTO text (id, text) VALUES (490,'E foi essa paixão que o levou a se embrenhar nas matas do Parque Nacional do Iguaçu, durante um ano inteiro, para produzir cinco mil fotos, das pegadas de homens e animais até a exuberância das Cataratas, passando pelo registro das mais diferentes espécies de aves e animais.');
INSERT INTO text (id, text) VALUES (491,'Era a realização do sonho adolescente, que, na esteira do ofício do pai, publicou aos 17 anos, na revista Manchete, seus primeiros textos e fotos sobre o meio ambiente');
INSERT INTO text (id, text) VALUES (492,'In addition to being a rare blend of photographer and reporter, Rio de Janeiro native Marcos Sá Corrêa has an artist soul and, above all, has a passion for nature.');
INSERT INTO text (id, text) VALUES (493,'This passion led hum to venture into the forests of the Iguaçu National Park for an entire year to take five thousand photos of human footprints and animal tracks and of the exuberant Falls, registering the most varied species of birds and animals.');
INSERT INTO text (id, text) VALUES (494,'Corrêa as able to fulfill a dream he had nurtured since he was a teenager when, at the age of 17, he published in Manchete magazine his first articles and photos about the environment');
INSERT INTO text (id, text) VALUES (495,'Mescla rara de fotógrafo y reportero, con texto primoroso y alma de artista, el nativo de Río de Janeiro, Marcos Sá Corrêa es, antes de todo, un enamorado por la naturaleza.');
INSERT INTO text (id, text) VALUES (496,'Y fue esa pasión que lo llevó a se embrenhar en las matas del Parque Nacional del Iguazú, durante un año entero, para producir cinco mil fotos, de las cogidas de hombres y animales hasta la exuberância de las Cataratas, pasando por el registro de las más diferentes especies de aves y animales.');
INSERT INTO text (id, text) VALUES (497,'Era la realización del sueño adolescente, que, en la esteira del ofício del padre, publicó a los 17 años, en la revista Manchete, sus primeros textos y fotos sobre el medio ambiente');
