INSERT INTO text (id, text) VALUES (612,'Essa exposição apresenta a visão panorâmica de toda região no ano de 1982, sinalizando onde fica o território brasileiro, argentino e paraguaio');
INSERT INTO text (id, text) VALUES (613,'This exhibition presents a panoramic view of the entire region in 1982s, indicating where is Brazil, Argentina and Paraguay');
